import React, {useState} from "react";
import SearchBarContainer from '../../containers/SearchBar/SearchBarContainer';
import {search} from '../../utils/yelp';

const sortByOptions = {
    "Best Match": "best_match",
    "Highest Rated": "rating",
    "Most Reviewed": "review_count",
    "Nearest": "nearest",
};

const SearchBar = ({searchYelp}) => {
    const [term, setTerm] = useState("restaurants");
    const [location, setLocation] = useState("New york");
    const [sort, setSort] = useState("best_match");
    const [activeOption, setActiveOption] = useState(null);

    const handleTermChange = ({target}) => {
        setTerm(target.value);
    }

    const handleLocationChange = ({target}) => {
        setLocation(target.value);
    }

    // Function to sort businesses by distance from nearest to farthest
    function sortByDistance(businesses) {
        const sortedBusinesses = [...businesses]; // Create a copy of the original array
        sortedBusinesses.sort((a, b) => {
            return a.distance - b.distance; // Sort by ascending distance
        });
        return sortedBusinesses;
    }

    // Function to sort businesses by distance from farthest to nearest
    function sortByDistanceDescending(businesses) {
        const sortedBusinesses = [...businesses]; // Create a copy of the original array
        sortedBusinesses.sort((a, b) => {
            return b.distance - a.distance; // Sort by descending distance
        });
        return sortedBusinesses;
    }


    const handleSortClick = (option) => {
        setSort(option);
        setActiveOption(option); // Update the active option state when an option is clicked
        // Check if the selected option is "Nearest"
        if (option === "Nearest") {
            // Sort businesses by distance from nearest to farthest
            const sortedBusinesses = sortByDistance(businesses);
            setBusinesses(sortedBusinesses);
        } else {
            // Handle other sorting options here
            // For example, if you have other sorting options implemented
        }
        // Trigger Yelp API request with updated sorting option
        searchYelp(term, location, option);
    }

    const handleSearchSubmit = (event) => {
        event.preventDefault();
        searchYelp(term, location, sort);
    }

    //Statefull component olduguna gore JSX render etmirik. JSX ucun stateless componente gonderirik.
    return (
        <SearchBarContainer
            sortByOptions={sortByOptions}
            handleSortClick={handleSortClick}
            activeOption={activeOption}
            handleTermChange={handleTermChange}
            handleLocationChange={handleLocationChange}
            handleSearchSubmit={handleSearchSubmit}
        />
    );
};

export default SearchBar;
