const apiKey = 'ntOuardRnPcYxlYcYnmBRLsA-irzRSlgfRL_VnJ2DatxoiMrsOHU1wQBniSuAQUn_aQBwL7HzKIZ1UGCxxksHllBS-BaMCR21w56ArHG9WVhH0VPIChepOrWmIGNZXYx'; // replace your api key here

function search(term, location, sortBy) {
    return fetch(`https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}&sort_by=${sortBy}`, {
        headers: {
            Authorization: `Bearer ${apiKey}`
        }
    }).then(response => {
        return response.json();
    }).then(jsonResponse => {
        if (jsonResponse.businesses) {
            return jsonResponse.businesses.map(business => ({
                id: business.id,
                imageSrc: business.image_url,
                name: business.name,
                address: business.location.address1,
                city: business.location.city,
                state: business.location.state,
                zipCode: business.location.zip_code,
                category: business.categories[0].title,
                rating: business.rating,
                reviewCount: business.review_count
            }));
        }
    });
}

module.exports = {search};
