import React, { useState } from "react";
import SearchBarContainer from '../../containers/SearchBar/SearchBarContainer';
// import { search } from '../../utils/yelp';

const sortByOptions = {
    "Best Match": "best_match",
    "Highest Rated": "rating",
    "Most Reviewed": "review_count",
};

const SearchBar = (searchYelp) => {
    const [term, setTerm] = useState("");
    const [location, setLocation] = useState("");
    const [sort, setSort] = useState("");
    const [activeOption, setActiveOption] = useState(null);

    // useEffect(() => {
    //     console.log(sort);
    // }, [sort]);

    const handleTermChange = ({target}) => {
        setTerm(target.value);
    }

    const handleLocationChange = ({target}) => {
        setLocation(target.value);
    }

    const handleSortClick = (option) => {
        setSort(option);
        setActiveOption(option); // Update the active option state when an option is clicked
    }

    const handleSearchSubmit = (event) => {
        event.preventDefault();
        // Set default values if location or sort is not provided
        const searchLocation = location || "New York"; // Default location
        const searchSort = sort || "best_match"; // Default sort option
        searchYelp.search(term, searchLocation, searchSort)
            .then(businesses => {
                // Handle fetched businesses
                console.log('search.js', businesses);
            })
            .catch(error => {
                // Handle error
                console.error('Error fetching businesses:', error);
            });
    }

    //Statefull component olduguna gore JSX render etmirik. JSX ucun stateless componente gonderirik.
    return (
        <SearchBarContainer
            sortByOptions={sortByOptions}
            handleSortClick={handleSortClick}
            activeOption={activeOption}
            handleTermChange={handleTermChange}
            handleLocationChange={handleLocationChange}
            handleSearchSubmit={handleSearchSubmit}
        />
    );
};

export default SearchBar;
